package JavaClasses;import android.content.Context;import android.content.SharedPreferences;import com.mctc.android.InfiniteNotesTest.FragmentNote;import java.util.*;/** * Created by Administrator on 11/6/2014. */// method returns a list of note item dataSource objectspublic class NoteDataProvider{   // Initialize member variables starting with a string constant to return data   private static final String PREFERENCES = "notes";   private SharedPreferences notePreferences;   public NoteDataProvider(Context context)   {      notePreferences = context.getSharedPreferences(PREFERENCES, Context.MODE_PRIVATE);   }   public List <NoteItemData> findAll()   {      // map with get all option      Map <String, ? > noteMap = notePreferences.getAll();      // Sort map for display by date by string value using TreeSet      // which sorts the data automatically for you with a for loop.      // for findAll method      SortedSet <String> keys = new TreeSet <String> (noteMap.keySet());      List <NoteItemData> noteList = new ArrayList <NoteItemData>();      for(String key : keys )      {         NoteItemData note = new NoteItemData();         note.setKey(key);         note.setText((String) noteMap.get(key));         noteList.add(note);      }      return noteList;   }//END method NoteDataSource   // method to save a note to persistent data   public boolean update( NoteItemData note )   {      SharedPreferences.Editor editor = notePreferences.edit();      editor.putString(note.getKey(),note.getText());      editor.commit();      return true;   }//END bool update   // method to remove a note from persistent data   public boolean remove( NoteItemData note)   {      if (notePreferences.contains(note.getKey()))      {         SharedPreferences.Editor editor = notePreferences.edit();         editor.remove(note.getKey());         editor.commit();      }      return true;   }//END bool remove}//ENd class NoteDataProvider